import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Form, Button, Container, Alert } from 'react-bootstrap';

const EmployeeForm = () => {
  const baseURL = "http://localhost:5000/api/users";
  const navigate = useNavigate();
  const [enteredName, setName] = useState('');
  const [enteredEmail, setEmail] = useState('');


  const nameChangeHandler = (event) => {
    setName(event.target.value);
  };

  const emailChangeHandler = (event) => {
    setEmail(event.target.value);
  };


  const submitActionHandler = (event) => {
    event.preventDefault();
    axios
      .post(baseURL, {
        username: enteredName,
        email: enteredEmail
      })
      .then((response) => {
        alert("User " + enteredName + " added!");
        navigate("/read");
      }).catch(error => {
        alert("error===" + error);
      });

  };

  const cancelHandler = () => {
    //reset the values of input fields
    setName('');
    setEmail('');
    navigate("/read");

  }
  return (
    <Alert variant='primary'>
      <Container>
        <Form onSubmit={submitActionHandler}>
          <Form.Group controlId="form.Username">
            <Form.Label>UserName</Form.Label>
            <Form.Control type="text" value={enteredName} onChange={nameChangeHandler} placeholder="Enter Username" required />
          </Form.Group>
          <Form.Group controlId="form.Email">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" value={enteredRole} onChange={emailChangeHandler} placeholder="Enter email" required />
          </Form.Group>
          <br></br>
          <Button type='submit'>Add Employee</Button>
          &nbsp;&nbsp;&nbsp;
          <Button type='submit' onClick={() => cancelHandler()}>Cancel</Button>
        </Form>

      </Container>
    </Alert>

  );
}
export default EmployeeForm;
